$('#login-button').click(function (event) {
	event.preventDefault();
	var pwd = document.getElementById("pwd").value;

	if (pwd === "1127") {
		// 密码正确，隐藏表单并添加动画
		$('form').fadeOut(500);
		$('.wrapper').addClass('form-success');
		requestFullScreen();
		setTimeout(function () {
			location.href = "index1.html";
		}, 2000);
	} else {
		// 密码错误，显示提示信息
		$('#error-message').show();
		alert("密码是你的生日哦!（密码为4位数）");
	}
});

function requestFullScreen(element) {
	var element = document.documentElement;
	// 判断各种浏览器，找到正确的方法
	var requestMethod = element.requestFullScreen || // W3C
		element.webkitRequestFullScreen || // Chrome等
		element.mozRequestFullScreen || // FireFox
		element.msRequestFullScreen; // IE11
	if (requestMethod) {
		requestMethod.call(element);
	} else if (typeof window.ActiveXObject !== "undefined") { // for Internet Explorer
		var wscript = new ActiveXObject("WScript.Shell");
		if (wscript !== null) {
			wscript.SendKeys("{F11}");
		}
	}
}

function changeFrameHeight() {
    // 获取 iframe 元素
    var ifm = document.getElementById("iframepage");
    
    // 检查 iframe 是否存在
    if (ifm) {
        // 设置 iframe 的高度为浏览器窗口高度
        ifm.style.height = document.documentElement.clientHeight + "px";
    } else {
        // 如果 iframe 不存在，输出警告信息
        console.warn("iframe with ID 'iframepage' not found.");
    }
}

// 确保在 DOM 加载完成后运行
document.addEventListener("DOMContentLoaded", function () {
    // 页面加载后调整 iframe 高度
    changeFrameHeight();
    // 监听窗口大小变化事件并调整高度
    window.addEventListener("resize", changeFrameHeight);
});


window.onresize = function () {
	changeFrameHeight();
}
